mapper.update:
UPDATE NEWS_INFO
SET visible = #{visible}
WHERE news_id = #{newsId}

mapper.insert:
INSERT INTO NEWS_KEYWORD_MAPPING (
    news_id,
    keyword_id
) VALUES (
    #{newsId},
    #{keywordId}
)
ON CONFLICT DO NOTHING

mapper.select:
SELECT EXISTS(
    SELECT 1
    FROM NEWS_INFO
    WHERE link = #{link}
)

SELECT news_id
FROM NEWS_INFO
WHERE link = #{link}
